import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { IoMdAdd, IoMdClose, IoMdRemove } from 'react-icons/io';
import { CartContext } from '../contexts/CartContext'

const CartItem = ({item}) => {
  const { removeFromCart, increaseAmount, decreaseAmount } = useContext(CartContext)
  const {id, title, price, image, amount} = item;
    return ( 
    <div className='flex gap-x-4 py-2 lg:px-6 border-b border-gray-200 w-full font-light text-gray-500'>
      <div className='w-full min-h-[150px] flex items-center gap-x-4'>
        <Link to={`/product/${id}`}>
          <img className='max-w-[80px] hover:scale-105' src={image} alt={title}/>
        </Link>
        <div className='w-full flex flex-col'>
          <div className='flex justify-between mb-2'>
            <Link to={`/product/${id}`} className='text-sm uppercase font-medium max-w-[240px] text-primary hover:underline'>{title}</Link>
          <div className='text-xl cursor-pointer'>
            <IoMdClose onClick={() => removeFromCart(id)} className='text-grey-500 hover:text-red-500 transition' />
          </div>
          </div>
          <div className='flex text-sm gap-x-2 h-[36px]'>
            <div className='flex flex-1 max-w-[100px] items-center h-full border text-primary font-medium'>
              <div className='flex-1'><IoMdRemove /></div>
              <div className='flex-1'>{amount}</div>
              <div className=''><IoMdAdd onClick={() => increaseAmount(id)}/></div>
            </div>

            <div className='flex-1 flex items-center justify-around'>$ {price}</div>

            <div className='text-primary font-medium flex-1 flex items-center justify-end'>{`$ ${parseFloat(price * amount).toFixed(2)}`}</div>
          </div>
        </div>
      </div>
    </div>
);
};

export default CartItem;
