import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { IoMdArrowForward } from 'react-icons/io';
import { FiTrash2 } from 'react-icons/fi';
import CartItem from '../components/CartItem';
import { SidebarContext } from '../contexts/SidebarContext';
import { CartContext } from '../contexts/CartContext';

const Sidebar = () => {
  const {isOpen, toggleSidebar} =  useContext(SidebarContext)
  const {cart, total, itemAmount} = useContext(CartContext)
  return <div className={`${
    isOpen ? 'right-0' : '-right-full' 
    } w-full bg-white fixed top-0 h-full shadow-2xl md:w-[35vw] xl:max-w-]30vw] transition-all duration-300 z-20 px-4 lg:px-[35px]`}
    ><div className='flex items-center justify-between py-6 border-b'>
    <div className='uppercase text-sm font-semibold'>Shopping Bag ({itemAmount})</div>
      <div onClick={toggleSidebar} className='cursor-pointer w-8 h-8 flex justify-center items-center'>
        <IoMdArrowForward className='text02xl' />
      </div>
    </div>
    <div className='flex flex-col gap-y-2 h-[500px] lg:h-[600px] overflow-y-auto overflow-x-hidden border-b'>
      <div>{cart.map((item) => {
        return <CartItem item={item} key={item.id} />
      })}</div></div>
      <div className='flex items-center justify-around py-6 border-b'><span>Total:</span>{`$ ${parseFloat(total).toFixed(2)}`}</div>
      <Link><FiTrash2></FiTrash2></Link>
      <Link to='/' className='bg-gray-200 flex p-4 items-center justify-around py-6 border-b'>View cart</Link>
      <Link to='/checkout' className='bg-primary flex items-center justify-around text-white p-6 border-b'>Checkout</Link>
    </div>;
};

export default Sidebar;
