  const { isOpen, setIsOpen} = useContext(SidebarContext)
  return (
    <div>
  <div>Header</div>
  <div onClick={() => setIsOpen(!isOpen)} className='cursor-pointer ' >
<BsBag className='text-2xl' />  
  </div>
  </div>
  ); 
};

export default Header;
